svg.script:
 
    
    var basePointX    = 150;
    var basePointY    = 120;
    var currentX      = 0;
    var currentY      = 0;
    var offsetX       = 0;
    var offsetY       = 0;
    var radius        = 0;
    var minorAxis     = 15;
    var majorAxis     = 30;
    var spiralCount   = 4;
    var Constant      = 0.20;
    var angle         = 0;
    var maxAngle      = 720;
    var angleDelta    = 3;
    var reflectX      = basePointX;
    var reflectY      = basePointY+
                          Constant*maxAngle;
    var shortPause    = 10;
    var rGap          = 8;
    var strokeWidth   = 1;
    var style         = "";
    var points        = "";
    var opacity       = 0.2;
    var rVal          = 0;
    var gVal          = 0;
    var bVal          = 0;
 
    var cBezierNode   = null;
    var rectNode      = null;
    var ellipseNode   = null;
    var svgDocument   = null;
    var gcNode        = null;
    var svgNS         = "http://www.w3.org/2000/svg";

    function init(evt) {
       svgDocument = evt.target.ownerDocument; 
       gcNode = svgDocument.getElementById("gc")

       drawSpiral();
    }

    function drawSpiral(event) {
       for(angle=0; angle<maxAngle; angle+=angleDelta) {
          radius = Constant*angle;
  
          offsetX = radius*Math.cos(angle*Math.PI/180);
          offsetY = radius*Math.sin(angle*Math.PI/180);

          currentX = basePointX+offsetX;
          currentY = basePointY-offsetY;
  
          rVal = angle*255/maxAngle;
          gVal = 0;
          bVal = 0;
 
          strokeColor = 
                  "rgb("+rVal+","+gVal+","+bVal+")";

          style  = "fill:"+strokeColor;
          if( angle % 3 == 0 ) {
             style += ";stroke:yellow";
          } else {
             style += ";stroke:green";
          }

          style += ";stroke-width:"+strokeWidth;

          ellipseNode = document.createElementNS(svgNS, "ellipse");
          ellipseNode.setAttribute("style",style);
          ellipseNode.setAttribute("cx", currentX);
          ellipseNode.setAttribute("cy", currentY);
          ellipseNode.setAttribute("rx", majorAxis);
          ellipseNode.setAttribute("ry", minorAxis);
          gcNode.appendChild(ellipseNode);

          // render reflected ellipse...
          currentX = reflectX+offsetX;
          currentY = reflectY+offsetY;

          ellipseNode = document.createElementNS(svgNS, "ellipse");
          ellipseNode.setAttribute("opacity",opacity);
          ellipseNode.setAttribute("style",style);
          ellipseNode.setAttribute("cx", currentX);
          ellipseNode.setAttribute("cy", currentY);
          ellipseNode.setAttribute("rx", majorAxis);
          ellipseNode.setAttribute("ry", minorAxis);
          gcNode.appendChild(ellipseNode);

          drawCBezierArc();
       }
    } // drawSpiral

    function drawCBezierArc() {
       // outer bezier arc...
       points  = "m"+(currentX+majorAxis/2)+","+(currentY)+" ";

       points += "a"+(majorAxis/2)+","+
                     (majorAxis/2)+" ";
       points +=     "0 0,0 "+(majorAxis)+",0 ";

       points += "a"+(majorAxis/2)+","+
                     (majorAxis/2)+" ";
       points +=     "0 0,0 "+(majorAxis)+",0 ";

       points += "C"+(currentX+majorAxis/2)+","+
                     (currentY-5*majorAxis/2)+" ";
       points +=     (currentX+3*majorAxis/2)+","+
                     (currentY)+" ";
       points +=     (currentX)+","+
                     (currentY+2*majorAxis)+" ";

     //cBezierNode = svgDocument.createElement("path");
       cBezierNode = svgDocument.createElementNS(svgNS, "path");
       cBezierNode.setAttribute("d", points);
       cBezierNode.setAttribute("style", style);
       gcNode.appendChild(cBezierNode);

       // inner bezier arc...
       points  = "m"+(currentX)+","+(currentY)+" ";
       points += "C"+(currentX+majorAxis)+","+
                     (currentY-3*majorAxis/2)+" ";
       points +=     (currentX+majorAxis/2)+","+
                     (currentY)+" ";
       points +=     (currentX)+","+
                     (currentY+2*majorAxis)+" ";

     //cBezierNode = svgDocument.createElement("path");
       cBezierNode = svgDocument.createElementNS(svgNS, "path");
       cBezierNode.setAttribute("d", points);
       cBezierNode.setAttribute("style", "fill:white");
       gcNode.appendChild(cBezierNode);
    }
