svg.script:
 
    
     var basePointX      = 200;
     var basePointY      = 250;
     var minorAxis       = 50;
     var majorAxis       = 100;
     var ellipticX       = 0;
     var ellipticY       = 0;
     var style           = "";
     var fillColors      = ['red','#880088','blue','yellow'];
     var fillCount       = 4;
     var strokeColors    = ['#880088','white','yellow','red'];
     var strokeCount     = 4;
     var strokeWidths    = [1, 4, 8];
     var widthCount      = 3;
     var dashStyles      = ["4 1 4 1", "2 8 2 8", "1 16 1 16"];
     var dashCount       = 3;
     var cylinderHeight  = 200;
 
     var ellipseNode     = null;
     var svgDocument     = null;
     var gcNode          = null;
     var svgNS           = "http://www.w3.org/2000/svg";
 
     function init(evt) {
        svgDocument = evt.target.ownerDocument; 
        gcNode = svgDocument.getElementById("gc")

        drawSpiral();
     }

     function drawSpiral() {
        for(var z=0; z<cylinderHeight; z++) {
           ellipticX = majorAxis*Math.cos(z*Math.PI/180);
           ellipticY = minorAxis*Math.sin(z*Math.PI/180);

           if( z < cylinderHeight - minorAxis/2 ) {
              style = "fill:"+fillColors[z%fillCount];
           } else {
              style = "fill:none";
           }

           style += ";stroke:"+strokeColors[z%strokeCount];
           style += ";stroke-dasharray:"+dashStyles[z%dashCount];
           style += ";stroke-width:"+strokeWidths[z%widthCount];

         //ellipseNode = svgDocument.createElement("ellipse");
           ellipseNode = document.createElementNS(svgNS, "ellipse");
           ellipseNode.setAttribute("style",style);
           ellipseNode.setAttribute("cx",   basePointX+ellipticX);
           ellipseNode.setAttribute("cy",   basePointY-ellipticY-z);
           ellipseNode.setAttribute("rx",   majorAxis);
           ellipseNode.setAttribute("ry",   minorAxis);
           gcNode.appendChild(ellipseNode);
        }
     } // drawSpiral
