svg.script:
 
    
    var width         = 800;
    var height        = 500;
    var basePointX    = 250;
    var basePointY    = 150;
    var currentX      = 0;
    var currentY      = 0;
    var offsetX       = 0;
    var offsetY       = 0;
    var radius        = 0;
    var minorAxis     = 12;
    var majorAxis     = 20;
    var shiftUp       = 2*minorAxis;
    var spiralCount   = 4;
    var Constant      = 0.25;
    var angle         = 0;
    var maxAngle      = 720;
    var angleDelta    = 4;
    var shortPause    = 10;
    var strokeWidth   = 1;
    var style         = "";
    var points        = "";
    var rVal          = 0;
    var gVal          = 0;
    var bVal          = 0;
 
    var ellipseNode   = null;
    var svgDocument   = null;
    var gcNode        = null;
    var bgNode        = null;
    var svgNS         = "http://www.w3.org/2000/svg";

    function init(evt) {
       svgDocument = evt.target.ownerDocument; 
       gcNode = svgDocument.getElementById("gc")

       drawBackground();
       drawSpiral();
    }

    function drawBackground() {
       points = "";
       for(angle=0; angle<maxAngle; angle+=angleDelta) {
          radius = Constant*angle;
  
          offsetX = radius*Math.cos(angle*Math.PI/180);
          offsetY = radius*Math.sin(angle*Math.PI/180);

          currentX = basePointX+offsetX;
          currentY = basePointY-offsetY;

          points += currentX+","+currentY+" ";
       }

       style="fill:url(#circle3DEffect1)";
       bgNode = svgDocument.createElementNS(svgNS, "polygon");
       bgNode.setAttribute("style",style);
       bgNode.setAttribute("points", points);
       gcNode.appendChild(bgNode);
    }

    function drawSpiral() {
       for(angle=0; angle<maxAngle; angle+=angleDelta) {
          radius = Constant*angle;
  
          offsetX = radius*Math.cos(angle*Math.PI/180);
          offsetY = radius*Math.sin(angle*Math.PI/180);

          currentX = basePointX+offsetX;
          currentY = basePointY-offsetY;
  
          rVal = angle*255/maxAngle;
          gVal = 0;
          bVal = 0;
 
          strokeColor = "rgb("+rVal+","+gVal+","+bVal+")";

          style  = "fill:"+strokeColor;
          style += ";stroke:yellow";
          style += ";stroke-width:"+strokeWidth;

          ellipseNode = document.createElementNS(svgNS, "ellipse");
          ellipseNode.setAttribute("style",style);
          ellipseNode.setAttribute("cx", currentX);
          ellipseNode.setAttribute("cy", currentY);
          ellipseNode.setAttribute("rx", majorAxis);
          ellipseNode.setAttribute("ry", minorAxis);
          gcNode.appendChild(ellipseNode);

          currentX = basePointX+offsetX;
          currentY = basePointY-offsetY-shiftUp;
  
          ellipseNode = document.createElementNS(svgNS, "ellipse");
          ellipseNode.setAttribute("style",style);
          ellipseNode.setAttribute("cx", currentX);
          ellipseNode.setAttribute("cy", currentY);
          ellipseNode.setAttribute("rx", majorAxis);
          ellipseNode.setAttribute("ry", minorAxis);
          gcNode.appendChild(ellipseNode);
       }
    } // drawSpiral
