svg.script:
 
   
    var basePointX    = 300;
    var basePointY    = 250;
    var currentX      = 0;
    var currentY      = 0;
    var offsetX       = 0;
    var offsetY       = 0;
    var sine1         = 0;
    var sine2         = 0;
    var cosine1       = 0;
    var cosine2       = 0;
    var minorAxis     = 10;
    var majorAxis     = 20;
    var shiftUp       = 2*minorAxis;
    var shiftLeft     = majorAxis;
    var A             = 120;
    var B             = 20;
    var C             = 10;
    var factor        = A/B;
    var Constant      = 120;
    var angle         = 0;
    var angle1        = 0;
    var angle2        = 0;
    var maxAngle      = 360;
    var angleDelta    = 4;
    var strokeWidth   = 2;
    var style         = "";
    var rVal          = 0;
    var gVal          = 0;
    var bVal          = 0;
 
    var ellipseNode   = null;
    var cBezierNode   = null;
    var svgDocument   = null;
    var gcNode        = null;
    var svgNS         = "http://www.w3.org/2000/svg";

    function init(evt) {
       svgDocument = evt.target.ownerDocument; 
       gcNode = svgDocument.getElementById("gc")

       drawSpiral();
    }

    function drawSpiral() {
       for(angle=0; angle<maxAngle; angle+=angleDelta) {
          angle1  = angle*Math.PI/180;
          angle2  = angle*Math.PI/180*(factor+1);

          sine1   = Math.sin(angle1);
          sine2   = Math.sin(angle2);
          cosine1 = Math.cos(angle1);
          cosine2 = Math.cos(angle2);

          offsetX = (A+B)*cosine1 - C*cosine2;
          offsetY = (A+B)*sine1   - C*sine2;

          currentX = basePointX+offsetX;
          currentY = basePointY-offsetY;
  
          rVal = angle*255/maxAngle;
          gVal = 0;
          bVal = 0;

          fillColor = "rgb("+rVal+","+gVal+","+bVal+")";
          strokeColor = "rgb("+rVal+","+gVal+","+bVal+")";

          style  = "fill:"+strokeColor;
          style += ";stroke:yellow";
          style += ";stroke-width:"+strokeWidth;

          ellipseNode = document.createElementNS(svgNS, "ellipse");
          ellipseNode.setAttribute("cx", currentX);
          ellipseNode.setAttribute("cy", currentY);
          ellipseNode.setAttribute("rx", majorAxis);
          ellipseNode.setAttribute("ry", minorAxis);
          gcNode.appendChild(ellipseNode);

          currentX = basePointX+offsetX-shiftLeft;
          currentY = basePointY-offsetY-shiftUp;
  
          ellipseNode = document.createElementNS(svgNS, "ellipse");
          ellipseNode.setAttribute("style",style);
          ellipseNode.setAttribute("cx", currentX);
          ellipseNode.setAttribute("cy", currentY);
          ellipseNode.setAttribute("rx", majorAxis);
          ellipseNode.setAttribute("ry", minorAxis);
          gcNode.appendChild(ellipseNode);

          drawCBezierArc();
       }
    } // drawSpiral

    function drawCBezierArc() {
       // outer bezier arc...
       points  = "m"+(currentX)+","+(currentY)+" ";
       points += "C"+(majorAxis)+","+(-5*majorAxis/4)+" ";
       points +=     (0)+","+(4*majorAxis/2)+" ";
       points +=     (0)+","+(3*majorAxis/2)+" ";
       points += "S"+(0)+","+(3*majorAxis/2)+" ";
       points +=     (0)+","+(4*majorAxis/2)+" ";

       style  = "fill:red;stroke-width:8";
       style += ";stroke-dasharray:8 8 8 8";

       cBezierNode = svgDocument.createElementNS(svgNS, "path");
       cBezierNode.setAttribute("d", points);
       cBezierNode.setAttribute("style", style);
       gcNode.appendChild(cBezierNode);

       // inner bezier arc...
       points  = "m"+(currentX)+","+(currentY)+" ";
       points += "C"+(2*majorAxis/2)+","+(-3*majorAxis/4)+" ";
       points +=     (majorAxis/2)+","+(0)+" ";
       points +=     (0)+","+(2*majorAxis)+" ";

       cBezierNode = svgDocument.createElementNS(svgNS, "path");
       cBezierNode.setAttribute("d", points);
       cBezierNode.setAttribute("style", "stroke:red;stroke-width:8");
       gcNode.appendChild(cBezierNode);
    }
