svg.script:
 
    
    var basePointX    = 450;
    var basePointY    = 350;
    var currentX      = 0;
    var currentY      = 0;
    var offsetX       = 0;
    var offsetY       = 0;
    var radius        = 0;
    var minorAxis     = 15;
    var majorAxis     = 30;
    var spiralCount   = 4;
    var Constant      = 300;
    var angle         = 0;
    var maxAngle      = 720;
    var angleDelta    = 2;
    var rGap          = 20;
    var rWidth        = 2*majorAxis;
    var rHeight       = 2*minorAxis;
    var widthDelta    = 4;
    var heightDelta   = 2;
    var shortPause    = 10;
    var strokeWidth   = 1;
    var xVertex       = 0;
    var yVertex       = 0;
    var slantX        = 20;
    var slantY        = 10;
    var rectCount     = 0;
    var points        = "";
    var strokeColor   = "";
    var style         = "";
    var rVal          = 0;
    var gVal          = 0;
    var bVal          = 0;
    var sineCubed     = 0;
    var cosineCubed   = 0;

    var rectNode      = null;
    var polygonNode   = null;
    var ellipseNode   = null;
    var svgDocument   = null;
    var gcNode        = null;
    var svgNS         = "http://www.w3.org/2000/svg";

    function init(evt) {
       svgDocument = evt.target.ownerDocument; 
       gcNode = svgDocument.getElementById("gc")

       drawSpiral();
    }

    function drawSpiral() {
       for(angle=0; angle<maxAngle; angle+=angleDelta) {
          sineCubed = Math.sin(angle*Math.PI/180)*
                      Math.sin(angle*Math.PI/180)*
                      Math.sin(angle*Math.PI/180);

          cosineCubed = Math.cos(angle*Math.PI/180)*
                        Math.cos(angle*Math.PI/180)*
                        Math.cos(angle*Math.PI/180);

          radius = Constant*(sineCubed-cosineCubed);
  
          offsetX = radius*Math.cos(angle*Math.PI/180);
          offsetY = radius*Math.sin(angle*Math.PI/180);

          currentX = basePointX+offsetX;
          currentY = basePointY-offsetY;
  
          rVal = angle*255/maxAngle;
          gVal = 0;
          bVal = 0;

          strokeColor = "rgb("+rVal+","+gVal+","+bVal+")";

          style  = "fill:"+strokeColor;
          style += ";stroke:yellow";
          style += ";stroke-width:"+strokeWidth;

          ellipseNode = document.createElementNS(svgNS, "ellipse");
          ellipseNode.setAttribute("style",style);
          ellipseNode.setAttribute("cx", currentX);
          ellipseNode.setAttribute("cy", currentY);
          ellipseNode.setAttribute("rx", majorAxis);
          ellipseNode.setAttribute("ry", minorAxis);
          gcNode.appendChild(ellipseNode);

          addWatch();

          if( angle % rGap == 0 ) {
             addRectangleNode();
             rWidth  += widthDelta;
             rHeight += heightDelta;
          }
       }
    } // drawSpiral

    function addWatch() {
       // add watch face...
       rectNode = svgDocument.createElementNS(svgNS, "rect");
       rectNode.setAttribute("x",     currentX);
       rectNode.setAttribute("y",     currentY);
       rectNode.setAttribute("width", 80);
       rectNode.setAttribute("height",80);
       rectNode.setAttribute("style",
                   "fill:url(#blueCircle);stroke:yellow");
       gcNode.appendChild(rectNode);

       // add four time dots...
       ellipseNode = document.createElementNS(svgNS, "ellipse");
       ellipseNode.setAttribute("style","fill:url(#redCircle)");
       ellipseNode.setAttribute("cx", currentX+40);
       ellipseNode.setAttribute("cy", currentY+10);
       ellipseNode.setAttribute("rx", 6);
       ellipseNode.setAttribute("ry", 6);
       gcNode.appendChild(ellipseNode);

       ellipseNode = document.createElementNS(svgNS, "ellipse");
       ellipseNode.setAttribute("style","fill:url(#redCircle)");
       ellipseNode.setAttribute("cx", currentX+70);
       ellipseNode.setAttribute("cy", currentY+40);
       ellipseNode.setAttribute("rx", 6);
       ellipseNode.setAttribute("ry", 6);
       gcNode.appendChild(ellipseNode);

       ellipseNode = document.createElementNS(svgNS, "ellipse");
       ellipseNode.setAttribute("style","fill:url(#redCircle)");
       ellipseNode.setAttribute("cx", currentX+40);
       ellipseNode.setAttribute("cy", currentY+70);
       ellipseNode.setAttribute("rx", 6);
       ellipseNode.setAttribute("ry", 6);
       gcNode.appendChild(ellipseNode);

       ellipseNode = document.createElementNS(svgNS, "ellipse");
       ellipseNode.setAttribute("style","fill:url(#redCircle)");
       ellipseNode.setAttribute("cx", currentX+10);
       ellipseNode.setAttribute("cy", currentY+40);
       ellipseNode.setAttribute("rx", 6);
       ellipseNode.setAttribute("ry", 6);
       gcNode.appendChild(ellipseNode);

       // add hour hand...
       points  = (currentX+40)+","+(currentY+40)+" ";
       points += (currentX+55)+","+(currentY+44)+" ";
       points += (currentX+60)+","+(currentY+40)+" ";
       points += (currentX+55)+","+(currentY+36)+" ";
       points += (currentX+40)+","+(currentY+40)+" ";

       polygonNode = svgDocument.createElementNS(svgNS, "polygon");
       polygonNode.setAttribute("style","fill:white");
       polygonNode.setAttribute("points", points);
       gcNode.appendChild(polygonNode);

       // add minute hand...
       points  = (currentX+40)+","+(currentY+40)+" ";
       points += (currentX+42)+","+(currentY+15)+" ";
       points += (currentX+40)+","+(currentY+10)+" ";
       points += (currentX+38)+","+(currentY+15)+" ";
       points += (currentX+40)+","+(currentY+40)+" ";

       polygonNode = svgDocument.createElementNS(svgNS, "polygon");
       polygonNode.setAttribute("style","fill:white");
       polygonNode.setAttribute("points", points);
       gcNode.appendChild(polygonNode);
    }

    function addRectangleNode() {
     //style  = "fill:yellow;stroke:red;";
       style  = "fill:url(#gradientDefinition2);";
       style += "stroke-width:"+strokeWidth;

       rectNode = svgDocument.createElementNS(svgNS, "rect");
       rectNode.setAttribute("style",style);
       rectNode.setAttribute("x",      currentX-rWidth/2);
       rectNode.setAttribute("y",      currentY-rHeight/2);
       rectNode.setAttribute("width",  rWidth);
       rectNode.setAttribute("height", rHeight);
       gcNode.appendChild(rectNode);

       // top horizontal strip... 
       xVertex = currentX-rWidth/2;
       yVertex = currentY-rHeight/2;
       points  = xVertex+" "+yVertex+" ";

       xVertex = currentX-rWidth/2+rWidth;
       yVertex = currentY-rHeight/2;
       points  += xVertex+" "+yVertex+" ";

       xVertex  = currentX-rWidth/2+rWidth+slantX;
       yVertex  = currentY-rHeight/2-slantY;
       points  += xVertex+" "+yVertex+" ";

       xVertex  = currentX-rWidth/2+slantX;
       yVertex  = currentY-rHeight/2-slantY;
       points  += xVertex+" "+yVertex;

       if( rectCount % 4 == 0 ) {
          style  = "fill:yellow;stroke:red;";
          style += "stroke-width:"+strokeWidth;
       } else if( rectCount % 4 == 1 ) {
          style  = "fill:blue;stroke:red;";
          style += "stroke-width:"+strokeWidth;
       }

       polygonNode = svgDocument.createElementNS(svgNS, "polygon");
       polygonNode.setAttribute("style",style);
       polygonNode.setAttribute("points",points);
       gcNode.appendChild(polygonNode);

       // right vertical strip... 
       xVertex = currentX-rWidth/2+rWidth;
       yVertex = currentY-rHeight/2;
       points  = xVertex+" "+yVertex+" ";

       xVertex  = currentX-rWidth/2+rWidth+slantX;
       yVertex  = currentY-rHeight/2-slantY;
       points  += xVertex+" "+yVertex+" ";

       xVertex  = currentX-rWidth/2+rWidth+slantX;
       yVertex  = currentY-rHeight/2-slantY+rHeight;
       points  += xVertex+" "+yVertex+" ";

       xVertex  = currentX-rWidth/2+rWidth;
       yVertex  = currentY-rHeight/2+rHeight;
       points  += xVertex+" "+yVertex+" ";

       if( rectCount % 4 == 2 ) {
          style  = "fill:blue;stroke:red;";
          style += "stroke-width:"+strokeWidth;
       } else if( rectCount % 4 == 3 ) {
          style  = "fill:blue;stroke:red;";
          style += "stroke-width:"+strokeWidth;
       }

       ++rectCount;

       polygonNode = svgDocument.createElementNS(svgNS, "polygon");
       polygonNode.setAttribute("style",style);
       polygonNode.setAttribute("points",points);
       gcNode.appendChild(polygonNode);
    }
